//import {ADD_FAV,REMOVE_FAV,FILTER,ORDER} from './actions';
import { ADD_FAV,REMOVE_FAV,FILTER } from "./actios-type";


const initialState = {
    myFavorites:[],
    allCharacters: [],
};

const reducer = (state=initialState,{type,payload})=>{
    switch (type) {
        case ADD_FAV:
            return {...state,
                myFavorites:[...state.allCharacters,payload],
                allCharacters:[...state.allCharacters,payload]
            }
        case REMOVE_FAV:
            return {...state,
                    myFavorites:state.myFavorites.filter((char)=>char.id !== payload)
            }
        case FILTER:
            const allCharactersFiltered=state.allCharacters.filter((char)=>char.gender === payload)
            return {...state,myFavorites:
                             payload==='all'
                             ?[...state.allCharacters] 
                             :allCharactersFiltered
            

            // return {...state,
            //         allCharacters:state.allCharacters.filter((char)=>char.gender !== payload),
            //         myFavorites:state.allCharacters
                }
        case ORDER:{
            const allCharactersOrder= [...state.allCharacters];
            return {...state,
                    myFavorites:
                    payload==='A'
                    ? allCharactersOrder.sort((a,b)=> a.id-b.id)
                    : allCharactersOrder.sort((a,b)=> b.id-a.id)}
                    
            // return {...state,
            //         allCharacters:state.allCharacters.sort((a,b)=>{
            //             if (payload==='A') return a.gender-b.gender
            //             else return b.gender-a.gender}),
            //         myFavorites:state.allCharacters    
            // }
        }        
        default: return {...state};
    }    
}

export default reducer;
